import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {ToastContainer,toast} from "react-toastify"

function Register() {
  const [userValues, setUserValues] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const toastOptions = {
    position: "bottom-right",
    autoclose: 7000,
    pauseOnHover: true,
    dragable: true,
    theme: "dark"
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    if(handleValidation()){
      alert("true")
    }
  };
  const handleChange = (event) => {
    setUserValues({ ...userValues, [event.target.name]: event.target.value });
  };

  const handleValidation = () => {
    if (userValues.username.length < 4) {
      return false;
      console.log("Username Should Be More Than 4 Charachters");
      toast.error("Username Should Be More Than 4 Charachters")
    }
    else if (userValues.username.length < 7) {
      return false;
      console.log("Email Should Be More Than 7 Charachters");
    }
    else if (userValues.password.length < 4) {
      return false;
      console.log("Password Should Be More Than 4 Charachters");
    }
    else if (userValues.password !== userValues.confirmPassword) {
      return false;
      console.log("Passwords Don't Match");
    }
    return true;

  };

  return (
    <>
      <div className="form-container">
        <form onSubmit={(event) => handleSubmit(event)}>
          <div className="brand">
            <img src="" alt="" />
            <h1>Typee</h1>
          </div>
          <input
            type="text"
            placeholder="Username"
            name="username"
            onChange={(e) => handleChange(e)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            name="email"
            onChange={(e) => handleChange(e)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            name="password"
            onChange={(e) => handleChange(e)}
            required
          />
          <input
            type="password"
            placeholder="Confirm Password"
            name="confirmPassword"
            onChange={(e) => handleChange(e)}
            required
          />
          <button type="submit">Create User</button>
          <span>
            Already Have an Account? <Link to="/login">Login</Link>
          </span>
        </form>
      </div>
      <ToastContainer/>
    </>
  );
}

export default Register;
